import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';  // Link와 useNavigate 가져오기
import './home.css';

function Home() {
  const [groups, setGroups] = useState([]);
  const [isPublic, setIsPublic] = useState(true);
  const navigate = useNavigate();  // useNavigate 훅 사용하여 페이지 이동

  useEffect(() => {
    // API 요청으로 그룹 데이터를 가져옵니다.
    fetchGroups();
  }, []);

  const fetchGroups = () => {
    // 그룹 목록을 불러오는 API 호출을 여기에 추가
    setGroups([
      { id: 1, title: '그룹 1', description: '새로운 추억을 만드는 그룹', dDay: 30, empathy: 150, memory: 5 },
      { id: 2, title: '그룹 2', description: '추억을 나누는 그룹', dDay: 20, empathy: 120, memory: 3 },
      // 더 많은 그룹 데이터 추가 가능
    ]);
  };

  const togglePublicPrivate = () => {
    setIsPublic(!isPublic);
  };

  const handleSearch = () => {
    // 검색 버튼 클릭 시 그룹 목록 페이지로 이동
    navigate('/groups');
  };

  return (
    <div className="page-container">
      <header>
        <h1>조각집</h1> {/* 프로젝트 제목 */}
        <Link to="/create-group" className="create-group-button">그룹 만들기</Link> {/* 그룹 만들기 버튼 */}
      </header>

      <div className="search-filter-container">
        <input type="text" className="search-bar" placeholder="그룹명을 입력해주세요" />
        
        <div className="toggle-container">
          <span>{isPublic ? '공개' : '비공개'}</span>
          <div className="toggle" onClick={togglePublicPrivate}>
            {isPublic ? '🔓' : '🔒'}
          </div>
        </div>

        <button className="search-button" onClick={handleSearch}>검색</button> {/* 검색 버튼 클릭 시 그룹 목록 페이지 이동 */}
      </div>

      <div className="group-list">
        {groups.map(group => (
          <Link to={`/groups/${group.id}`} key={group.id} className="group-box"> {/* 그룹 박스 클릭 시 상세 페이지 이동 */}
            <div className="group-image"></div>
            <div className="group-info">
              <div className="group-title">{group.title}</div>
              <div className="group-description">{group.description}</div>
              <div className="group-stats">
                <div className="group-d-day">D-{group.dDay}</div>
                <div className="group-empathy-count">공감 {group.empathy}개</div>
                <div className="group-memory-count">추억 {group.memory}개</div>
              </div>
            </div>
          </Link>
        ))}
      </div>

      <div className="load-more">
        <button>더보기</button>
      </div>
    </div>
  );
}

export default Home;
