import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';  // LinkÏôÄ useNavigate Í∞ÄÏ†∏Ïò§Í∏∞
import './home.css';

function Home() {
  const [groups, setGroups] = useState([]);
  const [isPublic, setIsPublic] = useState(true);
  const navigate = useNavigate();  // useNavigate ÌõÖ ÏÇ¨Ïö©ÌïòÏó¨ ÌéòÏù¥ÏßÄ Ïù¥Îèô

  useEffect(() => {
    // API ÏöîÏ≤≠ÏúºÎ°ú Í∑∏Î£π Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏ÏòµÎãàÎã§.
    fetchGroups();
  }, []);

  const fetchGroups = () => {
    // Í∑∏Î£π Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§Îäî API Ìò∏Ï∂úÏùÑ Ïó¨Í∏∞Ïóê Ï∂îÍ∞Ä
    setGroups([
      { id: 1, title: 'Í∑∏Î£π 1', description: 'ÏÉàÎ°úÏö¥ Ï∂îÏñµÏùÑ ÎßåÎìúÎäî Í∑∏Î£π', dDay: 30, empathy: 150, memory: 5 },
      { id: 2, title: 'Í∑∏Î£π 2', description: 'Ï∂îÏñµÏùÑ ÎÇòÎàÑÎäî Í∑∏Î£π', dDay: 20, empathy: 120, memory: 3 },
      // Îçî ÎßéÏùÄ Í∑∏Î£π Îç∞Ïù¥ÌÑ∞ Ï∂îÍ∞Ä Í∞ÄÎä•
    ]);
  };

  const togglePublicPrivate = () => {
    setIsPublic(!isPublic);
  };

  const handleSearch = () => {
    // Í≤ÄÏÉâ Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú Í∑∏Î£π Î™©Î°ù ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
    navigate('/groups');
  };

  return (
    <div className="page-container">
      <header>
        <h1>Ï°∞Í∞ÅÏßë</h1> {/* ÌîÑÎ°úÏ†ùÌä∏ Ï†úÎ™© */}
        <Link to="/create-group" className="create-group-button">Í∑∏Î£π ÎßåÎì§Í∏∞</Link> {/* Í∑∏Î£π ÎßåÎì§Í∏∞ Î≤ÑÌäº */}
      </header>

      <div className="search-filter-container">
        <input type="text" className="search-bar" placeholder="Í∑∏Î£πÎ™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî" />
        
        <div className="toggle-container">
          <span>{isPublic ? 'Í≥µÍ∞ú' : 'ÎπÑÍ≥µÍ∞ú'}</span>
          <div className="toggle" onClick={togglePublicPrivate}>
            {isPublic ? 'üîì' : 'üîí'}
          </div>
        </div>

        <button className="search-button" onClick={handleSearch}>Í≤ÄÏÉâ</button> {/* Í≤ÄÏÉâ Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú Í∑∏Î£π Î™©Î°ù ÌéòÏù¥ÏßÄ Ïù¥Îèô */}
      </div>

      <div className="group-list">
        {groups.map(group => (
          <Link to={`/groups/${group.id}`} key={group.id} className="group-box"> {/* Í∑∏Î£π Î∞ïÏä§ ÌÅ¥Î¶≠ Ïãú ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄ Ïù¥Îèô */}
            <div className="group-image"></div>
            <div className="group-info">
              <div className="group-title">{group.title}</div>
              <div className="group-description">{group.description}</div>
              <div className="group-stats">
                <div className="group-d-day">D-{group.dDay}</div>
                <div className="group-empathy-count">Í≥µÍ∞ê {group.empathy}Í∞ú</div>
                <div className="group-memory-count">Ï∂îÏñµ {group.memory}Í∞ú</div>
              </div>
            </div>
          </Link>
        ))}
      </div>

      <div className="load-more">
        <button>ÎçîÎ≥¥Í∏∞</button>
      </div>
    </div>
  );
}

export default Home;
