import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './GroupDetail.css';

// 더미 데이터
const dummyGroup = {
  id: 1,
  name: '달봉이네 가족',
  introduction: '서로 한 마음으로 응원하고 아끼는 달봉이네 가족입니다.',
  dDay: 'D+265',
  memoriesCount: 8,
  likesCount: 1500,
  badges: ['7일 연속 게시글 등록', '그룹 공감 1만 개 이상 받기', '추억 공감 1만 개 이상 받기'],
  imageUrl: 'https://via.placeholder.com/300',
};

const dummyMemories = Array.from({ length: 8 }, (_, index) => ({
  id: index + 1,
  groupName: '달봉이아들',
  title: '에델바이스 꽃말이 소중한 추억이래요',
  isPublic: true,
  tags: ['#인천', '#낚시'],
  date: '2024.01.19 18:00',
  likes: 120,
  comments: 8,
  imageUrl: 'https://via.placeholder.com/300',
}));

function GroupDetail() {
  const [group, setGroup] = useState(dummyGroup);
  const [memories, setMemories] = useState(dummyMemories);
  const [isPublic, setIsPublic] = useState(true);
  const [sortType, setSortType] = useState('최신순');
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  // 추억 올리기 버튼 클릭 핸들러
  const handleCreateMemory = () => {
    navigate('/PostCreate'); // PostCreate.js 경로로 이동
  };

  // 공개/비공개 토글
  const handleToggle = () => setIsPublic(!isPublic);

  // 검색
  const handleSearchInputChange = (e) => setSearchTerm(e.target.value);

  // 정렬 옵션 선택
  const handleSortChange = (e) => setSortType(e.target.value);

  return (
    <div className="group-detail-container">
      {/* 상단 헤더 */}
      <div className="memory-header">
        <h2>추억 목록</h2>
        <button className="create-memory-button" onClick={handleCreateMemory}>추억 올리기</button>
      </div>

      {/* 검색 및 필터 박스 */}
      <div className="memory-filter-box">
        <div className="toggle-container">
          <button
            className={`toggle-button ${isPublic ? 'active' : ''}`}
            onClick={handleToggle}
          >
            공개
          </button>
          <button
            className={`toggle-button ${!isPublic ? 'active' : ''}`}
            onClick={handleToggle}
          >
            비공개
          </button>
        </div>
        <input
          type="text"
          className="search-bar"
          placeholder="태그, 혹은 제목을 입력해주세요"
          value={searchTerm}
          onChange={handleSearchInputChange}
        />
        <select className="sort-select" value={sortType} onChange={handleSortChange}>
          <option value="최신순">최신순</option>
          <option value="댓글순">댓글순</option>
          <option value="공감순">공감순</option>
        </select>
      </div>

      {/* 추억 리스트 */}
      <div className="memory-list">
        {memories.map((memory) => (
          <div key={memory.id} className="memory-card">
            <img src={memory.imageUrl} alt="추억 이미지" />
            <div className="memory-info">
              <div className="memory-meta">
                <span>{memory.groupName}</span> | <span>{memory.isPublic ? '공개' : '비공개'}</span>
              </div>
              <h3>{memory.title}</h3>
              <div className="memory-tags">
                {memory.tags.map((tag, index) => (
                  <span key={index}>{tag}</span>
                ))}
              </div>
              <div className="memory-stats">
                <span>{memory.date}</span>
                <span>공감 {memory.likes}</span>
                <span>댓글 {memory.comments}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
      <button className="load-more">더보기</button>
    </div>
  );
}

export default GroupDetail;
