import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'; // Switch 대신 Routes 사용
import PublicGroupList from './group/components/PublicGroupList'; // PublicGroupList 페이지 불러오기
import GroupCreate from './group/components/GroupCreate'; // GroupCreate.js 파일을 임포트합니다.
import GroupCreateSuccess from './group/components/GroupCreateSuccess';
import ConfirmationModal from './components/ConfirmationModal';

function App() {
  const [showModal, setShowModal] = useState(false);

  const handleOpenModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);

  return (
    <div>
      <button onClick={handleOpenModal}>모달 열기</button>
      {showModal && (
        <ConfirmationModal
          title="그룹 생성 성공"
          message="그룹이 성공적으로 생성되었습니다."
          onClose={handleCloseModal}
        />
      )}
    </div>
  );
  return (
    <Router>
      <div>
        {/* Routes 사용하여 각 페이지 이동 */}
        <Routes>
          <Route exact path="/" element={<PublicGroupList />} /> {/* 루트 경로에서 공개 그룹 목록 페이지를 렌더링 */}
          <Route path="/GroupCreate" element={<GroupCreate />} /> {/* 그룹 만들기 페이지 */}
          <Route path="/GroupCreateSuccess" element={<GroupCreateSuccess />} />
        </Routes>

        {/* 컴포넌트들 렌더링 */}
        {/*<Group />    {/*Group 컴포넌트 렌더링 */}
        {/*<Post />     {/*Post 컴포넌트 렌더링 */}
        {/*<Comment /> {/* Comment 컴포넌트 렌더링 */}
        {/*<Badges />  {/* Badges 컴포넌트 렌더링 */}
      </div>
    </Router>
  );
  
}






export default App;  // App 컴포넌트를 내보내기
