import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './PostDetail.css';

function PostDetail() {
  const { memoryId } = useParams();
  const [memory, setMemory] = useState(null);

  // 더미 데이터
  const dummyMemory = {
    id: memoryId,
    title: '에델바이스 꽃말이 소중한 추억이래요',
    nickname: '달봉이아들',
    moment: '2024-01-19 18:00',
    location: '인천',
    content: '에델바이스는 소중한 추억을 의미합니다.',
    tags: ['#인천', '#낚시'],
    imageUrl: 'https://via.placeholder.com/300',
  };

  // 더미 데이터를 바로 상태에 설정
  useEffect(() => {
    // 실제 API를 사용하지 않고 더미 데이터를 설정합니다.
    setMemory(dummyMemory);
  }, [memoryId]);

  if (!memory) return <div>Loading...</div>;

  return (
    <div className="post-detail-container">
      <div className="post-image">
        <img src={memory.imageUrl} alt="추억 이미지" />
      </div>
      <div className="post-content">
        <h1>{memory.title}</h1>
        <p>작성자: {memory.nickname}</p>
        <p>작성일: {memory.moment}</p>
        <p>위치: {memory.location}</p>
        <p>내용: {memory.content}</p>
        <div className="post-tags">
          {memory.tags.map((tag, index) => (
            <span key={index} className="tag-item">{tag}</span>
          ))}
        </div>
      </div>
    </div>
  );
}

export default PostDetail;
