import React, { useState, useEffect } from 'react';
import './publicGroupList.css'; // CSS 파일 import

function PublicGroupList() {
  const [isPublic, setIsPublic] = useState(true);
  const [sortBy, setSortBy] = useState('공감순'); // 초기 정렬값
  const [sortOptionsVisible, setSortOptionsVisible] = useState(false); // 정렬 메뉴 토글
  const [groups, setGroups] = useState([]); // 그룹 목록

  // 더보기 기능
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);

  // API 호출로 그룹 데이터를 가져옴
  useEffect(() => {
    fetch(`/api/groups?page=${page}`)
      .then(res => res.json())
      .then(data => {
        if (data.groups.length > 0) {
          setGroups(prevGroups => [...prevGroups, ...data.groups]);
        } else {
          setHasMore(false); // 더 이상 데이터 없으면 더보기 버튼 숨김
        }
      });
  }, [page]);

  // 공개/비공개 버튼 핸들러
  const handlePublicClick = () => setIsPublic(true);
  const handlePrivateClick = () => setIsPublic(false);

  // 정렬 옵션 토글
  const toggleSortOptions = () => setSortOptionsVisible(!sortOptionsVisible);

  // 정렬 선택 핸들러
  const handleSortSelection = (option) => {
    setSortBy(option);
    setSortOptionsVisible(false);
  };

  // 더보기 버튼 핸들러
  const loadMoreGroups = () => setPage(prevPage => prevPage + 1);

  return (
    <div className="page-container">
      {/* 검색 및 필터 박스 */}
      <div className="filter-box">
        <div className="toggle-container">
          <div className={`toggle-button ${isPublic ? 'active' : ''}`} onClick={handlePublicClick}>
            공개
          </div>
          <div className={`toggle-button ${!isPublic ? 'active' : ''}`} onClick={handlePrivateClick}>
            비공개
          </div>
        </div>
        <button className="search-button">검색</button>
        <input type="text" className="search-bar" placeholder="그룹명을 입력해주세요" />
        
        <div className="sort-toggle-container">
          <button className="sort-toggle" onClick={toggleSortOptions}>
            {sortBy}
          </button>
          {sortOptionsVisible && (
            <div className="sort-options">
              <div onClick={() => handleSortSelection('공감순')}>공감순</div>
              <div onClick={() => handleSortSelection('댓글순')}>댓글순</div>
              <div onClick={() => handleSortSelection('추억순')}>추억순</div>
            </div>
          )}
        </div>
      </div>

      {/* 그룹 리스트 */}
      <div className="group-list">
        {groups.map((group, index) => (
          <div key={index} className="group-card">
            <div className="group-image-container">
              <img src={group.image} alt={group.name} className="group-image" />
            </div>
            <div className="group-details">
              <div className="group-meta">
                <span>{`D+${group.dDay}`}</span>
                <span>{group.isPublic ? '공개' : '비공개'}</span>
              </div>
              <h2 className="group-name">{group.name}</h2>
              <p className="group-description">{group.description}</p>
              <div className="group-stats">
                <span>획득 배지: {group.badges}</span>
                <span>추억: {group.memories}</span>
                <span>그룹 공감: {group.empathy}</span>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* 더보기 버튼 */}
      {hasMore && (
        <div className="load-more-box">
          <button className="load-more-button" onClick={loadMoreGroups}>더보기</button>
        </div>
      )}
    </div>
  );
}

export default PublicGroupList;
