import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'; // Switch 대신 Routes 사용
import Group from './group/group';   // Group 컴포넌트를 불러옵니다.
import Post from './post/post';      // Post 컴포넌트를 불러옵니다.
import Comment from './comment/comment'; // Comment 컴포넌트를 불러옵니다.
import Badges from './badges/badges';    // Badges 컴포넌트를 불러옵니다.
import Home from './pages/home';         // Home 페이지 불러오기
import PublicGroupList from './pages/PublicGroupList'; // 그룹 목록 페이지 불러오기
import GroupDetail from './pages/GroupDetail';        // 그룹 상세 페이지 불러오기
import GroupCreate from './pages/GroupCreate';        // 그룹 생성 페이지 불러오기
import PasswordInput from './pages/PasswordInput';    // 비밀번호 입력 페이지 불러오기

function App() {
  return (
    <Router>
      <div>
        <h1>조각집</h1>  {/* 프로젝트 제목 */}

        {/* Routes 사용하여 각 페이지 이동 */}
        <Routes>
          <Route exact path="/" element={<Home />} />
          <Route path="/groups" element={<PublicGroupList />} />
          <Route path="/groups/:id" element={<GroupDetail />} />
          <Route path="/create-group" element={<GroupCreate />} />
          <Route path="/password-input" element={<PasswordInput />} />
        </Routes>

        {/* 컴포넌트들 렌더링 */}
        <Group />   {/* Group 컴포넌트 렌더링 */}
        <Post />    {/* Post 컴포넌트 렌더링 */}
        <Comment /> {/* Comment 컴포넌트 렌더링 */}
        <Badges />  {/* Badges 컴포넌트 렌더링 */}
      </div>
    </Router>
  );
}

export default App;  // App 컴포넌트를 내보내기
